import {connect} from "react-redux";

import ProfileSettings from "./ProfileSettings";
import {changeConfirmPassword, changePassword, setAvatar} from "../../redux/ProfileSettingsReducer";
import {getAdverts, getUserProfile} from "../../redux/MyAdvertsReducer";
import {setLogout} from "../../redux/LoginReducer";

let mapStateToProps = (state) => {
  return {
    password: state.settings.password,
    confirmPassword: state.settings.confirmPassword,
    photos: state.settings.photos,

    user: state.profile.user
  }
}

const ProfileSettingsContainer = connect(mapStateToProps, {
  changePassword, changeConfirmPassword, setAvatar,
  getUserProfile, setLogout, getAdverts
})(ProfileSettings);

export default ProfileSettingsContainer;
