import React, {useState} from 'react';
import s from "./MainCharacteristic.module.css";
import Input from "../Input/Input";
import TextArea from "../TextArea/TextArea";

const MainCharacteristic = () => {

  const [header, setHeader] = useState('');
  const handleSetHeader = (event) => {
    setHeader(event.target.value);
  }

  const [price, setPrice] = useState('');
  const handleSetPrice = (event) => {
    setPrice(event.target.value);
  }

  const [description, setDescription] = useState('');
  const handleSetDescription = (event) => {
    setDescription(event.target.value);
  }

  console.log(header);

  return (
      <div className={s.mainCharacteristic}>
        <Input name={'Название объявления '} value={header} handle={handleSetHeader}/>
        <Input name={'Цена (руб.) '} value={price} handle={handleSetPrice}/>
        <TextArea name={'Описание '} value={description} handle={handleSetDescription}/>
      </div>
  );
};

export default MainCharacteristic;
