import React, {useState} from 'react';

import s from './ForgotPassword.module.css';
import AuthInput from "../AuthInput/AuthInput";

const ForgotPassword = (props) => {
  const [email, setEmail] = useState('');
  const handleSetEmail = (event) => { setEmail(event.target.value); }

  let error = <span/>;
  const resetPassword = () => {
    console.log(email.length)
    if (email.length < 10) {
      return <span>Укажите email или имя пользователя для смены пароля!</span>;
    }
    else { return <span/>; }
  }

  return (
      <div className={s.fp}>
        <div className={s.fr__background}/>
        <div className={s.fr__window}>
          <div className={s.fr__content}>
            <AuthInput name={'Логин или email'} placeholder={'Имя пользователя или email'} value={email} handle={handleSetEmail}/>
            {error}
            <button className={s.fr__btn} onClick={resetPassword}>Сменить пароль</button>
            <span className={s.fr__description}>Укажите адрес электронной почты или имя пользователя к которому привязан аккаунт</span>
          </div>
        </div>
      </div>
  );
};

export default ForgotPassword;
