import axios from "axios";
import {useLocation} from "react-router-dom";

const SET_IMMOVABLES = 'SET_IMMOVABLES';
const SET_TRANSPORT = 'SET_TRANSPORT';
const SET_HOUSEHOLD = 'SET_HOUSEHOLD';
const SET_HOUSES = 'SET_HOUSES';
const SET_APPARTAMENTS = 'SET_APPARTAMENTS';
const SET_CARS = 'SET_CARS';
const SET_MOTOCYCLES = 'SET_MOTOCYCLES';
const SET_APPLIANCE = 'SET_APPLIANCE';
const SET_FURNITURE = 'SET_FURNITURE';
const SET_NEW_ADS = 'SET_NEW_ADS';

const SET_ADVERTS = 'SET_ADVERTS';

let initialState = {

  adverts: [],

}

const advertsReducer = (state = initialState, action) => {

  switch (action.type) {

    case SET_ADVERTS:
      let newAdvertList = [...state.adverts];
      action.ads.map(ad => {
        newAdvertList = [...newAdvertList, ad]
      });

      return {
        ...state,
        adverts: newAdvertList,
      }


    default: return state;
  }
}

export const setAdverts = (props) => ({type: SET_ADVERTS, ads: props.ads, location: props.location});

export const getImmovables = (props) => {
  return (dispatch) => {
    axios.get(`/ad/category/id1/?limit=10&page=${props.page}`).then(
        /*response => response.data.map(ad => dispatch(setAdverts(ad)))*/
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getTransport = (props) => {
  return (dispatch) => {
    axios.get(`/ad/category/id2/?limit=10&page=${props.page}`).then(
        /*response => response.data.map(ad => dispatch(setAdverts(ad)))*/
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getHousehold = (props) => {
  return (dispatch) => {
    axios.get(`/ad/category/id3/?limit=10&page=${props.page}`).then(
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getHouses = (props) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id1/?limit=10&page=${props.page}`).then(
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getAppartaments = (props) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id2/?limit=10&page=${props.page}`).then(
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getCars = (props) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id3/?limit=10&page=${props.page}`).then(
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getMotocycles = (props) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id4/?limit=10&page=${props.page}`).then(
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getAppliance = (props) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id5/?limit=10&page=${props.page}`).then(
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getFurniture = (props) => {
  return (dispatch) => {
    axios.get(`/ad/subcategory/id6/?limit=10&page=${props.page}`).then(
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getNewAds = (props) => {
  return (dispatch) => {
    axios.get(`/ad/view?limit=10&page=${props.page}`).then(
        response => dispatch(setAdverts({ads: response.data, location: props.location}))
    )
  }
}

export const getAdverts = (props) => {
  return (dispatch) => {

    switch (props.location) {
      case '/':
        dispatch(getNewAds(page));
        break;

      case '/nedvijimost':
        dispatch(getImmovables(page));
        break;

      case '/transport':
        dispatch(getTransport(page));
        break;

      default:
        return null;
    }

  }
}

export default advertsReducer;
