import React, {useState} from 'react';

import s from './CreateAd.module.css'
import OptionalSelector from "./OptionalSelector/OptionalSelector";

const CreateAd = () => {

  const [selectCategory, setSelectCategory] = useState('');
  const handleSelectCategory = (event) => {
    console.log(event.target.value);
    setSelectCategory(event.target.value);
  }

  const [selectSubCategory, setSelectSubCategory] = useState('');
  const handleSelectSubCategory = (event) => {
    setSelectSubCategory(event.target.value);
  }

  let categoriesList = [
    {option: 'Недвижимость'},
    {option: 'Транспорт'},
    {option: 'Всё для дома'}
  ];

  let subCatList = [
      [
        {option: 'Квартиры'},
        {option: 'Дома'},
      ],
      [
        {option: 'Машины'},
        {option: 'Мотоциклы'},
      ],
      [
        {option: 'Бытовая техника'},
        {option: 'Мебель'},
      ]
  ];

  let getSelectedCategory = () => {
    let i;
    if (selectCategory === 'Недвижимость')
      return i = 0

    if (selectCategory === 'Транспорт')
      return i = 1;

    if (selectCategory === 'Всё для дома')
      return i = 2;
  }

  let listItemsToOptions = (listItems) => {
    return listItems.map(el => <option value={el.option}>{el.option}</option>);
  }

  const OptionalSelector = (props) => {
    return <div className={s.formContent}>
        <label className={s.label} >{props.label}</label>
        <select className={s.selector} value={props.value} onChange={(event => {props.handle(event)})}>
          {props.options}
        </select>
      </div>
  }

  return (
      <div className={s.content}>
        <h3 className={s.text}>Добавление объявления</h3>
        <div className={s.container}>
          <div className={s.contentForms}>
            <form className={s.form}>

              <OptionalSelector label={'Категория: '} options={listItemsToOptions(categoriesList)} handle={handleSelectCategory} value={selectCategory}/>
              <OptionalSelector label={'Подкатегория: '} options={listItemsToOptions(subCatList[2])} handle={handleSelectSubCategory} value={selectSubCategory}/>

              <div className={s.spaceLine}><br/></div>
            </form>
          </div>
        </div>
      </div>
  );
};

export default CreateAd;
