const SELECT_CATEGORY = 'SELECT_CATEGORY';
const SELECT_SUB_CATEGORY = 'SELECT_SUB_CATEGORY';

let initialState = {
  category: '',
  subcategory: ''
}

const selectCategoryReducer = (state, action) => {
  switch (action.type) {
    case SELECT_CATEGORY: return 'Ты меняешь категорию';
    case SELECT_SUB_CATEGORY: return 'Ты меняешь подкатегорию';

    default: return state;
  }
}

export const changeSelectCategory = (select) => ({ type: SELECT_CATEGORY, select })
export const changeSelectSubCategory = (select) => ({ type: SELECT_SUB_CATEGORY, select })

export default selectCategoryReducer;
