const GET_CURRENT_SUB_ID = 'GET_CURRENT_SUB_ID';

let initialState = {
  currentSubId: 0
}

const editAdvertReducer = (state = initialState, action) => {
  switch (action.type) {

    case GET_CURRENT_SUB_ID: {}
      return {
        ...state,
        currentSubId: action.subId
      }

    default: return state;
  }
}

export default editAdvertReducer;
