import React from 'react';
import s from "./AuthInput.module.css";

const AuthInput = (props) => {
  let placeholder = props.placeholder === '' ? '' : props.placeholder;
  let type = props.type === '' ? 'text' : props.type;
  let borderStyle = props.danger === true && props.value !== ' ' ? {border: 'red 1px solid'} : {};

  return (
      <div>
        <span className={s.input__name}>{props.name}</span>
        <input style={{border: 'red 1px solid', color: 'red'}} className={s.input__item} type={type} placeholder={placeholder} value={props.value} onChange={event => props.handle(event)}/>
      </div>
  );
};

export default AuthInput;
