import React, {useEffect, useState} from 'react';
import {Route, Routes, useLocation} from 'react-router-dom';

import Footer from "./Footer/Footer";

import Immovables from "./Categories/Immovables/Immovables";
import Transport from "./Categories/Transport/Transport";
import Household from "./Categories/Household/Household";

import CreateAdContainer from "./CreateAd/CreateAdContainer";
import NavBarContainer from "./Navbar/NavBarContainer";
import ViewAdContainer from "./ViewAd/ViewAdContainer";
import MyAdvertsContainer from "./Profile/MyAdverts/MyAdvertsContainer";
import ProfileSettingsContainer from "./Profile/Settings/ProfileSettingsContainer";
import AdvertsContainer from "./Adverts/AdvertsContainer";
import ViewUserContainer from "./ViewUser/ViewUserContainer";
import DataContainer from "./Data/DataContainer";

import {
  MAIN_PAGE,
  TRANSPORT, IMMOVABLES, HOUSEHOLD,
  APPARTAMENTS, HOUSES,
  CARS, MOTO,
  APPLIANCE, FURNITURE, AD_SEARCH
} from "./redux/UrlReducer";

import OAuth from "./Authentication/OAuth/OAuth";
import Categories from "./Categories/Categories";
import Loader from "./Loader/Loader";

const Router = (props) => {

  const locate = useLocation().pathname;

  let mainPage = props.mainPage;
  let adSearch = props.adSearch;

  let immovables = props.immovables;
  let transport = props.transport;
  let household = props.household;

  let appartaments = props.appartaments;
  let houses = props.houses;

  let cars = props.cars;
  let moto = props.moto;

  let appliance = props.appliance;
  let furniture = props.furniture;

  return <div>

      <div>
        <NavBarContainer/>
        {immovables !== null &&
            <div>
              <Routes>
                <Route path={MAIN_PAGE.url} element={<Categories/>}/>

                <Route path={IMMOVABLES.url} element={<Immovables/>}/>
                <Route path={TRANSPORT.url} element={<Transport/>}/>
                <Route path={HOUSEHOLD.url} element={<Household/>}/>

                <Route path={APPARTAMENTS.url} element={<Immovables/>}/>
                <Route path={HOUSES.url} element={<Immovables/>}/>

                <Route exact path={CARS.url} element={<Transport/>}/>
                <Route exact path={MOTO.url} element={<Transport/>}/>

                <Route exact path={APPLIANCE.url} element={<Household/>}/>
                <Route exact path={FURNITURE.url} element={<Household/>}/>

                <Route exact path={"/ad/create"} element={<CreateAdContainer/>}/>

                <Route exact path={"/ad/id:id"} element={<ViewAdContainer/>}/>

                <Route exact path={"/profile/advert"} element={<MyAdvertsContainer/>}/>
                <Route exact path={"/profile/settings"} element={<ProfileSettingsContainer/>}/>

                <Route exact path={"/user/id:id"} element={<ViewUserContainer/>}/>

              </Routes>

              {(locate === MAIN_PAGE.url || locate.includes(IMMOVABLES.url) || locate.includes(TRANSPORT.url) || locate.includes(HOUSEHOLD.url) || locate.includes(AD_SEARCH.url)) && <AdvertsContainer/>
              }
            </div>
        }

        {immovables === null &&
            <div>
              <Loader/>
              <DataContainer/>
            </div>
        }
        <Footer/>

        <DataContainer/>
        {/*<OAuth/>*/}

      </div>
      }
    </div>
};

export default Router;
