import React, {useState} from 'react';

import s from './CreateAd.module.css'
import OptionalSelector from "./OptionalSelector/OptionalSelector";
import SpaceLine from "../SpaceLine";

const CreateAd = () => {

  const [selectCategory, setSelectCategory] = useState('');
  const handleSelectCategory = (event) => {
    setSelectCategory(event.target.value);
  }

  const [selectSubCategory, setSelectSubCategory] = useState('');
  const handleSelectSubCategory = (event) => {
    setSelectSubCategory(event.target.value);
  }

  const [header, setHeader] = useState('НАЧАЛЬНОЕ ЗНАЧЕНИЕ');
/*  const handleHeader = (event) => {
    console.log(`[Хедер]: ${event.target.value}`);
    setHeader(event.target.value);
  }*/

  const [price, setPrice] = useState('');
  const handlePrice = (event) => {
    console.log(`[Цена]: ${event.target.value}`);
    setPrice(event.target.value);
  }

  const [description, setDescription] = useState('');
  const handleDescription = (event) => {
    setDescription(event.target.value);
  }


  let categoriesList = [
    {id: 1, option: 'Недвижимость'},
    {id: 2, option: 'Транспорт'},
    {id: 3, option: 'Всё для дома'}
  ];

  let subCatList = [
      [
        {id: 1, option: 'Квартиры'},
        {id: 2, option: 'Дома'},
      ],
      [
        {id: 3, option: 'Машины'},
        {id: 4, option: 'Мотоциклы'},
      ],
      [
        {id: 5, option: 'Бытовая техника'},
        {id: 6, option: 'Мебель'},
      ]
  ];

  let getSelectedCategory = () => {
    switch (selectCategory) {
      case 'Недвижимость': return 0; break;
      case 'Транспорт': return 1; break;
      case 'Всё для дома': return 2; break;
      default: return -1;
    }
  }

  let getSelectedSubCategory = () => {
    switch (selectSubCategory) {
      case 'Квартиры': case 'Дома': return 0; break;
      case 'Машины': case 'Мотоциклы': return 1; break;
      case 'Бытовая техника': case 'Мебель': return 2; break;

      default: return -1;
    }
  }

  let listItemsToOptions = (listItems) => {
    return listItems.map(el => <option key={el.id} value={el.option}>{el.option}</option>);
  }

  const OptionalSelector = (props) => {
    return <div className={s.formContent}>
        <label className={s.label} >{props.label}</label>
        <select className={s.selector} value={props.value} onChange={(event => {props.handle(event)})}>
          {props.options}
        </select>
      </div>
  }

  const Input = (props) => {
    return <div className={s.inputContent}>
      <span className={s.label}>{props.name}</span>
      <input className={`${s.input} ${s.selector}`} type="text" value={header} onChange={(event => setHeader(event.target.value))}/>
    </div>
  }

  const Textarea = (props) => {
    return <div className={s.textAreaContent}>
      <span className={s.label}>{props.name}</span>
      <textarea value={props.value} placeholder={'Описание'} className={s.textArea} onChange={(event => {props.handle(event)})}/>
    </div>
  }

  return (
      <div className={s.content}>
        <h3 className={s.text}>Добавление объявления</h3>
        <div className={s.container}>
          <div className={s.contentForms}>
            <form className={s.form}>

              <OptionalSelector label={'Категория '} options={listItemsToOptions(categoriesList)} handle={handleSelectCategory} value={selectCategory}/>
              {getSelectedCategory() !== -1 &&
                  <OptionalSelector label={'Подкатегория '} options={listItemsToOptions(subCatList[getSelectedCategory()])} handle={handleSelectSubCategory} value={selectSubCategory}/>
              }

              <SpaceLine/>

              {getSelectedSubCategory() !== -1 &&
                  <div className={s.mainCharacteristic}>
                    <Input name={'Название объявления '} handle={handleHeader} value={header}/>
                    <Input name={'Цена (руб.) '} handle={handlePrice} value={price}/>
                    <Textarea name={'Описание '} handle={handleDescription} value={description}/>
                  </div>
              }
            </form>
          </div>
        </div>
      </div>
  );
};

export default CreateAd;
