import React from 'react';
import s from './Adverts.module.css';
import Advert from "./Advert/Advert";

import {BrowserRouter, Router, Routes, Route, Switch} from "react-router-dom";

const listAd = [
  {
    "adId": "1",
    "imgPath": "kalina_4.jpg",
    "adName": "Продам купола",
    "price": "50.000",
    "desc": "Топ за свои деньги",
    "datePublish": "30.03.2020"
  },
  {
    "adId": "2",
    "imgPath": "kalina_4.jpg",
    "adName": "Куплю купола",
    "price": "30.000",
    "desc": "Топ за свои деньги",
    "datePublish": "30.03.2020"
  },
  {
    "adId": "3",
    "imgPath": "kalina_4.jpg",
    "adName": "Xiaomi",
    "price": "15000",
    "desc": "Топ за свои деньги",
    "datePublish": "30.03.2020"
  },
  {
    "adId": "4",
    "imgPath": "kalina_4.jpg",
    "adName": "Куплю велосипед",
    "price": "15000",
    "desc": "Топ за свои деньги",
    "datePublish": "30.03.2020"
  },
]

const Adverts = (props) => {

  let adverts = listAd.map( el => {<Advert imgPath={el.imgPath} adName={el.adName} price={el.price} />});

  return (
        <div className={s.content}>
          <div className={s.headerName}>
            <h4 className={s.h4}>{props.categoryName}</h4>
          </div>
          <div className={s.adverts}>
            <Advert imgPath={listAd[0].imgPath} adName={listAd[0].adName} price={listAd[0].price}
                    description={listAd[0].desc} datePublish={listAd[0].datePublish} adId={listAd[0].adId}/>
            <Advert imgPath={listAd[1].imgPath} adName={listAd[1].adName} price={listAd[1].price}
                    description={listAd[1].desc} datePublish={listAd[1].datePublish} adId={listAd[1].adId}/>
            <Advert imgPath={listAd[2].imgPath} adName={listAd[2].adName} price={listAd[2].price}
                    description={listAd[2].desc} datePublish={listAd[2].datePublish} adId={listAd[2].adId}/>
            <Advert imgPath={listAd[3].imgPath} adName={listAd[3].adName} price={listAd[3].price}
                    description={listAd[3].desc} datePublish={listAd[3].datePublish} adId={listAd[3].adId}/>

            <Advert imgPath={listAd[0].imgPath} adName={listAd[0].adName} price={listAd[0].price}
                    description={listAd[0].desc} datePublish={listAd[0].datePublish} adId={listAd[0].adId}/>
            <Advert imgPath={listAd[1].imgPath} adName={listAd[1].adName} price={listAd[1].price}
                    description={listAd[1].desc} datePublish={listAd[1].datePublish} adId={listAd[1].adId}/>
            <Advert imgPath={listAd[2].imgPath} adName={listAd[2].adName} price={listAd[2].price}
                    description={listAd[2].desc} datePublish={listAd[2].datePublish} adId={listAd[2].adId}/>
            <Advert imgPath={listAd[3].imgPath} adName={listAd[3].adName} price={listAd[3].price}
                    description={listAd[3].desc} datePublish={listAd[3].datePublish} adId={listAd[3].adId}/>
          </div>
        </div>
  );
};

export default Adverts;
