import React, {useState} from 'react';

import s from './CreateAd.module.css'
import OptionalSelector from "./OptionalSelector/OptionalSelector";
import SpaceLine from "../SpaceLine";
import Input from "./Input/Input";
import TextArea from "./TextArea/TextArea";
import MainCharacteristic from "./MainCharacteristic/MainCharacteristic";

const CreateAd = () => {

  const [selectCategory, setSelectCategory] = useState('Выберите');
  const handleSelectCategory = (event) => {
    setSelectCategory(event.target.value);
  }

  const [selectSubCategory, setSelectSubCategory] = useState('Выберите');
  const handleSelectSubCategory = (event) => {
    setSelectSubCategory(event.target.value);
  }

  let categoriesList = [
    {id: 1, option: 'Недвижимость'},
    {id: 2, option: 'Транспорт'},
    {id: 3, option: 'Всё для дома'}
  ];

  let subCatList = [
      [
        {id: 1, option: 'Квартиры'},
        {id: 2, option: 'Дома'},
      ],
      [
        {id: 3, option: 'Машины'},
        {id: 4, option: 'Мотоциклы'},
      ],
      [
        {id: 5, option: 'Бытовая техника'},
        {id: 6, option: 'Мебель'},
      ]
  ];

  let getSelectedCategory = () => {
    switch (selectCategory) {
      case 'Недвижимость': return 0; break;
      case 'Транспорт': return 1; break;
      case 'Всё для дома': return 2; break;
      default: return -1;
    }
  }

  let getSelectedSubCategory = () => {
    switch (selectSubCategory) {
      case 'Квартиры': return 0; break;
      case 'Дома': return 1; break;
      case 'Машины': return 2; break;
      case 'Мотоциклы': return 3; break;
      case 'Бытовая техника': return 4; break;
      case 'Мебель': return 5; break;

      default: return -1;
    }
  }

  let listItemsToOptions = (listItems) => {
    return listItems.map(el => <option key={el.id} value={el.option}>{el.option}</option>);
  }

  let characteristics;
  if (getSelectedSubCategory() === 0)
    characteristics = <div>0</div>
  if (getSelectedSubCategory() === 1)
    characteristics = <div>1</div>
  if (getSelectedSubCategory() === 2)
    characteristics = <div>2</div>
  if (getSelectedSubCategory() === 3)
    characteristics = <div>3</div>
  if (getSelectedSubCategory() === 4)
    characteristics = <div>4</div>
  if (getSelectedSubCategory() === 5)
    characteristics = <div>5</div>
  else characteristics = <div>NULL</div>


  return (
      <div className={s.content}>
        <h3 className={s.text}>Добавление объявления</h3>
        <div className={s.container}>
          <div className={s.contentForms}>
            <form className={s.form}>

              <OptionalSelector label={'Категория '} options={listItemsToOptions(categoriesList)} handle={handleSelectCategory} value={selectCategory}/>

              {getSelectedCategory() !== -1 &&
                  <OptionalSelector label={'Подкатегория '} options={listItemsToOptions(subCatList[getSelectedCategory()])} handle={handleSelectSubCategory} value={selectSubCategory}/>
              }

              <SpaceLine/>

              {getSelectedSubCategory() !== -1 &&
                  <MainCharacteristic/>
              }

              {characteristics}
            </form>
          </div>
        </div>
      </div>
  );
};

export default CreateAd;
