import React, {useEffect, useState} from 'react';
import s from './Adverts.module.css';
import Advert from "./Advert/Advert";
import {useLocation} from "react-router-dom";
import InfiniteScroll from "react-infinite-scroller";

const Adverts = (props) => {

/*  let advertsList = props.listAd.map(el => <Advert imgPath={el.imgPath} adName={el.header} price={el.price} description={el.desc} datePublish={el.datePublish}
    adId={el.adId} />);*/

  const location = useLocation().pathname;

  let adverts = [];
  let itemList = [];
  let hasMore = props.hasMore;

  const [page, setPage] = useState(0);
  const [isLoading, setIsLoading] = useState(false);

  const loadMore = () => {
    if (isLoading === false)
      setIsLoading(true);
  }

  useEffect( () => {
    setPage(page + 1);

    switch (location) {
      case '/':
        props.getNewAds(page);
        adverts = props.newAds;
        break;

      case '/nedvijimost':
        props.getImmovables(page);
        adverts = props.immovables;
        break;

      case '/transport':
        props.getTransport(page);
        adverts = props.transport;
        break;

      default:
        return;
    }

    setIsLoading(false);
    if (adverts.length > 0) {
      let newListAd = adverts.map(ad => {
            return <Advert adId={ad.adId}
                           photos={ad.photos === null ? [] : ad.photos}
                           header={ad.header}
                           description={ad.description} price={ad.price}
                           createdAt={ad.createdAt === null ? 'Нет информации' : ad.createdAt}/>
          }
      )

      itemList = [...itemList, newListAd];
      debugger;
    }

  }, [isLoading, props]);

  const getAdverts = (adverts) => {
    return adverts.map(ad => {
          return <Advert adId={ad.adId}
                         photos={ad.photos === null ? [] : ad.photos}
                         header={ad.header}
                         description={ad.description} price={ad.price}
                         createdAt={ad.createdAt === null ? 'Нет информации' : ad.createdAt}/>
        }
    );
  }

  return (
      <div className={s.content}>
        <div className={s.headerName}>
          <h4 className={s.h4}>Категория</h4>
        </div>
        <div className={s.adverts}>
          <InfiniteScroll
              pageStart={0}
              loadMore={loadMore}
              hasMore={hasMore}
              loader={<div className={s.loader} key={0}>Loading ...</div>}>
              {console.log(itemList)}
          </InfiniteScroll>
        </div>
      </div>
  );
};

export default Adverts;
