import React, {useEffect} from 'react';
import s from './Adverts.module.css';
import Advert from "./Advert/Advert";
import {useLocation} from "react-router-dom";

const Adverts = (props) => {

  let advertsList = props.listAd.map(el => <Advert imgPath={el.imgPath} adName={el.header} price={el.price} description={el.desc} datePublish={el.datePublish}
    adId={el.adId} />);

  const location = useLocation().pathname;



  return (
      <div className={s.content}>
        <div className={s.headerName}>
          <h4 className={s.h4}>{props.categoryName}</h4>
        </div>
        <InfiniteScroll
            pageStart={0}
            loadMore={loadFunc}
            hasMore={true}
            loader={<div className={s.loader} key={0}>Loading ...</div>}>
          <div className={s.adverts}>
            {advertsList}
          </div>
        </InfiniteScroll>

      </div>
  );
};

export default Adverts;
