import {connect} from "react-redux";

import {changeLogin, changePassword, setAuth} from "../../redux/LoginReducer";
import Login from "./Login";
import {setToggleForgotPassword, setToggleLogin, setToggleRegistration} from "../../redux/NavBarReducer";

let mapStateToProps = (state) => {
  return {
    login: state.auth.login,
    password: state.auth.password,

    toggleLogin: state.navbar.toggleLogin,
    toggleRegistration: state.navbar.toggleRegistration,
    toggleForgorPassword: state.navbar.toggleForgorPassword
  }
}

const LoginContainer = connect(mapStateToProps, {changeLogin, changePassword, setAuth, setToggleLogin, setToggleRegistration, setToggleForgotPassword})(Login);

export default LoginContainer;
